const getRGBStyle = ({ r, g, b, opacity }) =>
  `rgba(${r}, ${g}, ${b}, ${opacity})`

export const getColorsRGB = (colors) =>
  colors.map((color) => {
    const rgb = color[0]
      .replace(
        /^#?([a-f\d])([a-f\d])([a-f\d])$/i,
        (m, r, g, b) => `#${r}${r}${g}${g}${b}${b}`
